import { Supadata } from '@supadata/js';

const supadata = new Supadata({
  apiKey: 'YOUR_API_KEY',
});
const video = await supadata.youtube.video({
  id: 'https://youtu.be/dQw4w9WgXcQ', // can be id or url
});
console.log(video.title);

import { Supadata } from '@supadata/js';

const supadata = new Supadata({
  apiKey: 'YOUR_API_KEY',
});
const playlist = await supadata.youtube.playlist({
  id: 'https://www.youtube.com/playlist?list=PLlaN88a7y2_plecYoJxvRFTLHVbIVAOoc', // can be id or url
});
console.log(`Playlist ${playlist.title} contains ${playlist.videoCount} videos`);

import { Supadata } from '@supadata/js';

const supadata = new Supadata({
  apiKey: 'YOUR_API_KEY',
});
const videos = await supadata.youtube.playlist.videos({
  id: 'https://www.youtube.com/playlist?list=PLlaN88a7y2_plecYoJxvRFTLHVbIVAOoc', // can be id or url
  limit: 20, // Optional: limit the number of videos to fetch
});
console.log(`Found ${videos.videoIds.length} videos`);

import { Supadata } from '@supadata/js';

